      value: eventData.cyrl
    },
    {
      param: 'ep.percent_scrolled',
      value: eventData.percent_scrolled
    },
    {
      param: 'ep.plays_count',
      value: eventData.plays_count
    },
    /**
     * E-Commerce Main:
     */
    {
      param: 'cu', // Currency Code
      value: eventData.currency
    },
    {
