      value: undefined
    },
    {
      param: 'cs', // Creative Slot
      value: undefined
    },
    {
      param: 'lo', // Location id
      value: undefined
    },
    /**
     * Campaign Attribution:
     */
    {
      param: 'cm', // Campaign Medium
      value: undefined
    },
    {
      param: 'cs', // Campaign Source
      value: undefined
    },
    {
      param: 'cn', // Campaign Name
      value: undefined
    },
    {
