        {
          param: 'ds',
          value: item.discount // ??
        },
        {
          param: 'af',
          value: item.affiliation // ??
        }
      ];
      settings.push({
        param: 'pr' + item.index,
        value: value
          .filter((setting) => isValidParam(setting.value))
          .map((setting) => setting.param + setting.value)
          .join('~')
      });
    });
  }
  /**
