var itemsArr  = (data.ecommerce && data.ecommerce.items) || [];
var currency  = (data.ecommerce && data.ecommerce.currency) || 'USD';
var out       = [];

/* Preferred path: build from ecommerce.items */
for (var i = 0; i < itemsArr.length; i++) {
  var it    = itemsArr[i] || {};
  var price = it.price ? (it.price*1||0) : 0;

  out.push({
    id:         it.item_id || it.id || 'UNKNOWN_ID',
    quantity:   it.quantity || 1,
    item_price: price,
    currency:   currency
  });
}

/* Fallback: build at least one entry from content_ids */
if (!out.length && data.content_ids) {
  var ids = [].concat(data.content_ids);
  out.push({
    id:         ids[0] || 'UNKNOWN_ID',
    quantity:   1,
    item_price: (data.price*1)||0,
    currency:   currency
  });
}

return out;


